import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Clear any previous error messages

    // Only password match validation
    if (password !== confirmPassword) {
      setErrorMessage('Passwords do not match!');
      return;
    }

    setLoading(true); // Show loading spinner or indicator

    try {
      const response = await axios.post('http://localhost:5500/api/auth/signup', {
        email,
        password,
      });

      // If signup is successful
      alert('Signup successful! Please login.');
      navigate('/login');
    } catch (error) {
      setErrorMessage(error.response?.data?.error || 'Signup failed. Please try again.');
    } finally {
      setLoading(false); // Stop loading spinner or indicator
    }
  };

  return (
    <div className="signup-container">
      <form onSubmit={handleSignup} noValidate> {/* noValidate disables HTML5 validation */}
        <h2>Signup</h2>
        {errorMessage && <p className="error-message">{errorMessage}</p>} {/* Show error */}
        <input
          type="email"
          placeholder="Enter Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input
          type="password"
          placeholder="Confirm Password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Signing up...' : 'Signup'}
        </button>
      </form>
    </div>
  );
};

export default Signup;
