import React, { useEffect, useState, useRef } from 'react';
import Chart from 'chart.js/auto';
import './Dashboard.css';

const Dashboard = () => {
  const [spendingData, setSpendingData] = useState({ budget: 4000, spent: 1500 });
  const [assetsData, setAssetsData] = useState({ assets: 30000, debt: 12000 });
  const [transactionsData, setTransactionsData] = useState({ labels: [], amounts: [] });
  const [categoriesData, setCategoriesData] = useState({ labels: ['Home', 'Car', 'Food'], amounts: [1000, 500, 700] });

  const spendingChartRef = useRef(null);
  const assetsChartRef = useRef(null);
  const transactionsChartRef = useRef(null);
  const categoriesChartRef = useRef(null);

  useEffect(() => {
    const spendingChart = new Chart(spendingChartRef.current, {
      type: 'line',
      data: {
        labels: ['Spent', 'Remaining'],
        datasets: [{
          label: 'Spending',
          data: [spendingData.spent, spendingData.budget - spendingData.spent],
          borderColor: '#f39c12',
          borderWidth: 2
        }]
      }
    });

    const assetsChart = new Chart(assetsChartRef.current, {
      type: 'line',
      data: {
        labels: ['Assets', 'Debt'],
        datasets: [{
          label: 'Assets vs Debt',
          data: [assetsData.assets, assetsData.debt],
          borderColor: '#28a745',
          borderWidth: 2
        }]
      }
    });

    const transactionsChart = new Chart(transactionsChartRef.current, {
      type: 'line',
      data: {
        labels: transactionsData.labels,
        datasets: [{
          label: 'Transaction Amounts',
          data: transactionsData.amounts,
          borderColor: '#9b59b6',
          borderWidth: 2
        }]
      }
    });

    const categoriesChart = new Chart(categoriesChartRef.current, {
      type: 'line',
      data: {
        labels: categoriesData.labels,
        datasets: [{
          label: 'Category Spending',
          data: categoriesData.amounts,
          borderColor: '#3498db',
          borderWidth: 2
        }]
      }
    });

    return () => {
      spendingChart.destroy();
      assetsChart.destroy();
      transactionsChart.destroy();
      categoriesChart.destroy();
    };
  }, [spendingData, assetsData, transactionsData, categoriesData]);

  const handleSpendingSubmit = (e) => {
    e.preventDefault();
    const budget = parseFloat(e.target.budget.value);
    const spent = parseFloat(e.target.spent.value);
    setSpendingData({ budget, spent });
  };

  const handleAssetsSubmit = (e) => {
    e.preventDefault();
    const assets = parseFloat(e.target.assets.value);
    const debt = parseFloat(e.target.debt.value);
    setAssetsData({ assets, debt });
  };

  const handleTransactionSubmit = (e) => {
    e.preventDefault();
    const category = e.target.category.value;
    const amount = parseFloat(e.target.amount.value);
    setTransactionsData(prev => ({
      labels: [...prev.labels, category],
      amounts: [...prev.amounts, amount]
    }));
  };

  const handleCategoriesSubmit = (e) => {
    e.preventDefault();
    const category1 = e.target.category1.value;
    const category1Amount = parseFloat(e.target.category1Amount.value);
    const category2 = e.target.category2.value;
    const category2Amount = parseFloat(e.target.category2Amount.value);
    const category3 = e.target.category3.value;
    const category3Amount = parseFloat(e.target.category3Amount.value);
    setCategoriesData({
      labels: [category1, category2, category3],
      amounts: [category1Amount, category2Amount, category3Amount]
    });
  };

  return (
    <div className="dashboard">
      <div className="container-fluid p-3">
        <button className="section-button active" id="spending-btn">Monthly Spending</button>
        <button className="section-button" id="assets-btn">Assets vs Debt</button>
        <button className="section-button" id="transactions-btn">Transactions</button>
        <button className="section-button" id="categories-btn">Top Categories</button>
      </div>

      <div className="container-fluid p-4">
        <div className="section-content active-section" id="spending-section">
          <div className="card p-3 spending-card">
            <h6>Monthly Spending</h6>
            <form onSubmit={handleSpendingSubmit}>
              <input type="number" name="budget" placeholder="Enter budget" required />
              <input type="number" name="spent" placeholder="Enter amount spent" required />
              <button type="submit" className="btn btn-primary btn-sm">Update Spending</button>
            </form>
            <canvas ref={spendingChartRef} className="chart-small mt-3"></canvas>
          </div>
        </div>

        <div className="section-content" id="assets-section">
          <div className="card p-3 assets-card">
            <h6>Assets vs Debt</h6>
            <form onSubmit={handleAssetsSubmit}>
              <input type="number" name="assets" placeholder="Enter total assets" required />
              <input type="number" name="debt" placeholder="Enter total debt" required />
              <button type="submit" className="btn btn-primary btn-sm">Update Assets</button>
            </form>
            <canvas ref={assetsChartRef} className="chart-small mt-3"></canvas>
          </div>
        </div>

        <div className="section-content" id="transactions-section">
          <div className="card p-3 transaction-card">
            <h6>Transactions</h6>
            <form onSubmit={handleTransactionSubmit}>
              <input type="text" name="category" placeholder="Category" required />
              <input type="number" name="amount" placeholder="Amount" required />
              <button type="submit" className="btn btn-primary btn-sm">Add Transaction</button>
            </form>
            <canvas ref={transactionsChartRef} className="chart-small mt-3"></canvas>
          </div>
        </div>

        <div className="section-content" id="categories-section">
          <div className="card p-3 categories-card">
            <h6>Top Categories</h6>
            <form onSubmit={handleCategoriesSubmit}>
              <input type="text" name="category1" placeholder="Category 1" required />
              <input type="number" name="category1Amount" placeholder="Amount 1" required />
              <input type="text" name="category2" placeholder="Category 2" required />
              <input type="number" name="category2Amount" placeholder="Amount 2" required />
              <input type="text" name="category3" placeholder="Category 3" required />
              <input type="number" name="category3Amount" placeholder="Amount 3" required />
              <button type="submit" className="btn btn-primary btn-sm">Update Categories</button>
            </form>
            <canvas ref={categoriesChartRef} className="chart-small mt-3"></canvas>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
